commits.content:
label {
    -webkit-transform: translateY(50%);
    -moz-transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    -o-transform: translateY(-50%);
    transform: translateY(50%);
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    transition: all 0.2s ease-in-out;
    position: absolute;
    top: -11%;
    background: none;
    color: #4e4e4e;
    font-weight: normal;
    cursor: text;
    pointer-events: none;
    font-family: sans-serif;
}

.text-field label {
    top:40%
}

input,
textarea {
    display: block;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
    -webkit-transition: border-color;
    -moz-transition: border-color;
    transition: border-color;
    box-sizing: border-box;
    resize: none;
    background-color: transparent;
    border: none;
    border-bottom: 1px solid #9e9e9e;
    font-family: sans-serif;
    font-size: 100%;
    padding: 1.5% 0% 1.5% 0%;
    width: 100%;
}

input:focus,
textarea:focus {
    border: none;
    border-bottom: 1px solid #9e9e9e;
}

input:focus ~ label,
input.hascontent ~ label {
    top: -70%;
    font-size: 0.8em;
    padding: 0 0.3em;
    border: none;
    background: #f9f9f9;
}

textarea:focus ~ label,
textarea.hascontent ~ label {
    top: -12%;
    font-size: 0.8em;
    padding: 0 0.3em;
    border: none;
    background: #f9f9f9;
}

.input-field {
    position: relative;
    display: block;
    clear: both;
    width: 45%;
}

.text-field {
    position: relative;
    display: block;
    clear: both;
    width: 95%;
}

.text-box-contact {
    display: flex;
    width: 97%;
    height: 350px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
}

.form-container {
    border-radius: 3px;
    width: 60%;
    height: 50%;
    background: rgba(255, 252, 252, 0.7);
    box-shadow: 0 15px 35px rgba(50,50,93,.1),0 5px 15px rgba(0,0,0,.07);
}

.form-flex {
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    height: 100%;
}

@media (max-width: 900px) {
    .text-box-contact {
        position: absolute;
        width: 96%;
        height: 350px;
        left: 2%;
        top: 62%;
        filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
        background: white;
    }
}
