commits.content:
html,
body {
    width: 100%;
    height: 100%;
}

body {
    margin: 0px;
}

a.button {
    font-family: Arial, Helvetica, sans-serif, sans-serif;
    position: absolute;
    padding-left: 11%;
    top: 60%;
    left: 45%;
    transform: translate(-50%, -50%);

    width: 170px;
    height: 40px;
    line-height: 2;
    font-size: 18px;
    font-weight: bold;
    letter-spacing: 1px;
    border: 3px solid #3b3c37;
    background: #787b70;
    color: white;
    border-radius: 40px;
    cursor: pointer;
    overflow: hidden;
    transition: all 0.35s;
}

a.button:hover {
    color: #abafa0;
}

a.button span {
    opacity: 1;
    visibility: visible;
    transition: all 0.35s;
}

.image-fit {
    max-width: 100%;
    max-height: 100%;
}

.background {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

    background: linear-gradient(
        180deg,
        #d3d8c6 0%,
        rgba(211, 216, 198, 0) 100%
    );
}

.logo-text {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-self: center;
    justify-items: center;
    align-items: center;
    order: 1;

    height: 8%;

    font-family: Six Caps;
    font-style: normal;
    font-weight: normal;
    font-size: 28px;
    line-height: 0%;
    text-decoration-line: line-through;
}

.semi-background {
    order: 2;
    flex-direction: column;
    width: 94%;
    height: 83%;
    left: 72px;
    top: 70px;

    background: linear-gradient(180deg, #abafa0 0%, rgba(84, 84, 84, 0) 100%);
}

.socialmedia-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: row;
    order: 3;
    height: 254px;
}

.socialmedia-icon {
    width: 100px;
    height: 70px;
}

.content {
    position: relative;
    order: 2;

    height: 100%;
}

.content-flex {
    display: flex;
    order: 2;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;

    height: 93%;
}

.profile-pic {
    position: absolute;
    width: 350px;
    height: 600px;
    left: 818px;
    top: 120px;

    /* background: url(0BEF8EAD-E3A8-48E9-A432-49B0476B3662.jpg); */
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
}

.header {
    position: absolute;
    width: 700px;
    height: 400px;
    left: 191px;
    top: 390px;

    font-family: "Six Caps", "Lora", serif;
    font-style: normal;
    font-weight: 800;
    font-size: 40px;
    line-height: 84px;

    color: #ffffff;

    text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
}

.text-box-about {
    position: absolute;
    width: 700px;
    height: 400px;
    left: 80px;
    top: 260px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));

    background: rgba(255, 252, 252, 0.7);
}

.about-pic {
    position: relative;
    width: 432px;
    height: 576px;
    left: 40.1%;
    top: 16px;
}

.about-text {
    position: absolute;
    width: 600px;
    height: 300px;
    left: 64px;
    top: 16px;

    font-family: "Six Caps", "Lora", serif;
    font-style: normal;
    font-weight: 800;
    font-size: 15px;
    line-height: 30px;

    color: black;
}

.work-pic {
    display: flex;
    flex-direction: column;
    align-items: center;

    width: 432px;
    height: 576px;
}

.text-work {

    width: 46%;
    height: 24%;

    font-family: "Open Sans", sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 230%;
    line-height: 85%;

    color: black;
}

.navbar {
    display: flex;
    order: 1;
    justify-content: space-between;
    justify-self: flex-end;

}

.nav-links {
    display: flex;
    justify-self: flex-end;
    justify-content: space-around;
}

.nav-link {
    font-family: "Six Caps", Roboto;
    font-style: normal;
    font-weight: 800;
    font-size: 42px;
    line-height: 30px;
    padding: 14px 16px;

    color: #3b3c37;

    text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    text-decoration: none;
}
.nav-link:hover {
    color: white;
}
.selected-link {
    color: white;
    text-decoration: underline;
}

.container {
    position: absolute;
    width: 700px;
    height: 300px;
    left: 180px;
    top: 352px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    background: rgba(255, 252, 252, 0.7);
}

.flex-row {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-around;
}

.flex-col {
    display: flex;
    flex-direction: column;
    align-items: center;
}

@media (max-width: 900px) {
    .background {
        width: 100%;
        height: 100%;

        align-items: center;

        background: #ffffff;
    }

    .semi-background {
        order: 2;
        display: flex;
        width: 88%;
        height: 99%;
        background: linear-gradient(
            180deg,
            #e0c5a3 0%,
            rgba(190, 194, 178, 0) 100%
        );
    }

    .header {
        position: absolute;
        left: 6%;
        top: 50%;

        /* position: unset; */
        width: 700px;
        height: 400px;

        font-family: "Six Caps", Arial, Helvetica, sans-serif, sans-serif;
        font-style: normal;
        font-weight: 400;
        font-size: 60px;
        line-height: 126px;

        color: #ffffff;

        text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    }

    .profile-pic {
        position: absolute;
        left: 46%;
        top: 24%;
        width: 350px;
        height: 600px;

        /* background: url(0BEF8EAD-E3A8-48E9-A432-49B0476B3662.jpg); */
        filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    }

    .text-work {
        /* position: absolute;
        left: 320px;
        top: 400px; */

        width: 77%;
        height: 24%;

        font-family: "Roboto", "Open Sans", sans-serif;
        font-style: normal;
        font-weight: 200;
        font-size: 145%;
        line-height: 111%;

        color: black;
        /* background: rgba(255, 252, 252, 0.7); */
    }
}

@media (max-width: 601px) {
    .semi-background {
        position: absolute;
        width: 361px;
        height: 799px;
        left: 33px;
        top: 97px;
        background: linear-gradient(
            180deg,
            #e0c5a3 0%,
            rgba(190, 194, 178, 0) 100%
        );
    }
}
